mapper.update:
UPDATE lock_info
update_time = now(), `lock_no` = #{lock_no}, `address` = #{address}, latitude = #{latitude}, longitude = #{longitude}, qr_code_no = #{qr_code_no}, device_no = #{device_no}, state = #{state}, status = #{status}, update_author = #{update_author},
WHERE id = #{id}

mapper.select:
select a.* from lock_info a

    and a.`state` = #{state}
    and a.status = #{status}
    and a.lock_no = #{lock_no}
